INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'经过保时捷认证的二手车是否能够被清晰的识别？（例如：车窗标签）','Could the Porsche Approved Used vehicles displayed outside be clearly identified? (e.g. window label) ','-车号牌标签或者车窗标签说明该车是经过保时捷认证的（两种标识方式有一种就可以）\r\n-如果户外没有二手车展示，请选择N/A','- Number plate branding or window labels signalling Porsche approved vehicle (one of the two is sufficient)\r\n- N/A if there are no used cars displayed outside ',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,' 在户外展示的汽车是否有清晰的信息说明？','Were the vehicles displayed outside clearly marked with information?','-车型、发动机类型、附件、价格、购车的交易套餐，\r\n-关于二氧化碳的排放；\r\n-如果没有汽车在户外展出，请选择N/A','\'- Model, engine type, accessories, price, financial packages\r\n- Reference to Co2 emissions\r\n- N/A if there are no cars displayed outside',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'在户外展出的车辆布局是否清晰？','Were the vehicles displayed outside presented in a clearly laid-out manner?','-清晰的新车与二手车展区划分\r\n- Customer cars set apart客户已购车区与展区分离\r\n-展示的车辆可从各个角度进行参观\r\n-如果没有汽车在户外展出，请选择N/A','-Clear division of new and used cars\r\n-Vehicles can be viewed from all sides\r\n-N/A if there are no cars displayed outside',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'店面外面是否干净整洁？','Was the outside area clean and tidy?','- 没有垃圾\r\n- 周围没有杂物摆放\r\n- 地面及建筑物干净','- No rubbish\r\n- No objects lying around\r\n- Clean building and surfaces ',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'在外展示的车辆是否干净整洁？','Were the vehicles displayed outside clean?','-没有来自驾驶过程中的灰尘\r\n-没有灰尘或表现出前几天天气状况的印记\r\n- 没有因为以前参观而留下的手指印或油渍\r\n- 如果户外没有汽车展示，请选择N/A','- No dirt from driving 没有来自驾驶过程中的灰尘\r\n- No dirt or marking from weather conditions of previous days\r\n- No fingerprints or visible smears from previous visits\r\n- N/A if there are no cars displayed outside',1,'','',1);
