INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'展区是否干净整洁？','Was the showroom clean and tidy?\r\n','-没有垃圾；\r\n- 周围没有多余的物品；\r\n- 没有污垢；\r\n- 干净；\r\n- 没有灰尘在植物或者展示品上\"\r\n','\"- No rubbish；\r\n- No unnecessary objects lying around；\r\n- free of dirt；\r\n- tidy；\r\n- No dust on plants or display cases',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'店面内的氛围是否令人愉快?\r\n','Was the atmosphere in the dealership pleasant?','- 没有背景噪音的干扰\r\n- 没有技术工人\r\n- 舒适的温度\r\n- 舒适的照明\r\n','- No disturbances from background noise- No technical staff\r\n- Pleasant temperature\r\n- Pleasant lighting\r\n',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'这里是否陈列着关于可选配件的产品说明小册子和信息材料？\r\n','Were product brochures and information material about additional options displayed？','作为一个消费者，是否很容易理解和区分保时捷品牌和产品的细节\r\n','As a customer, was it easy to understand and distinguish between the Porsche brand and product details',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'在室内展出的车辆布局是否清晰？\r\n','Were the vehicles displayed inside presented in a clearly laid-out manner?\r\n','- 清晰的新车与二手车展区划分\r\n- 客户已购车区与展区分离（例如：为即将交付给客户的车辆设置一个独立的区域）\r\n- 展示的车辆可从各个角度进行参观\"\r\n','\"- Clear division of new and used cars\r\n- Customer cars set apart (for example a seperate area for the delivery to customers)\r\n- Vehicles can be viewed from all sides\r\n',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'是否有Carrera, Boxster, Cayenne and Cayman车型在进行展示？\r\n','Were a Carrera, Boxster, Cayenne and Cayman exhibited?','If a model range was missing, please specify which.','If a model range was missing, please specify which.\r\n',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'在室内展示的车辆是否干净整洁？\r\n','Were the vehicles displayed inside clean?\r\n','-所有展示的车辆应该做到：\r\n- 汽车外表没有灰尘\r\n- 没有因为以前参观而留下的手指印或油渍\r\n- 车辆内部干净整洁\r\n','- All displayed vehicles should have\r\n- No dirt or dust on the vehicle\r\n- No fingerprints or visible smears from previous viewings\r\n- Clean interior',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'在室内展示的车辆是否有清晰的信息说明？\r\n','Were the vehicles displayed inside clearly marked with information?\r\n','- 车型、发动机类型、附件、价格、购车的金融套餐，\r\n- 关于二氧化碳的排放；\r\n- 请随机检查2-3辆汽车\"\r\n','- 车型、发动机类型、附件、价格、购车的金融套餐，\r\n- 关于二氧化碳的排放；\r\n- 请随机检查2-3辆汽车\r\n',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'经过保时捷认证的二手车是否能够被清晰的识别？\r\n','Could the Porsche Approved Used vehicles displayed inside be clearly identified?\r\n','- 车号牌标签或者车窗标签说明该车是经过保时捷认证的（两种标识方式有一种就可以）\r\n- 如果没有二手车展示，或有一个独立的二手车销售场所，请选择N/A\r\n','- Number plate branding or window labels signalling Porsche approved vehicle (one of the two is sufficient)\r\n- N/A if there are no used cars displayed inside or if a separate sales room for used cars is available',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'如果有附件/特殊设计，或零配件在所展示的车子出现，那么这些物件的详细资料是否被额外的标志表示出来？\r\n','If Tequipment/Exclusive or Aftermarket Accessories was on any of the cars, were the details of these made clearly visible with additional signage?','- 如果不明显或不能确认，请选择N/A\r\n','- N/A if not noticeable/identifiable\r\n',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (5,'评价（请务必详细作答）','Comments','请就A2这部分的检测做一个整体评价。恰当信息将帮助我们更深入地理解您所处的情景。\r\n','Please comment, concluding the dimension. If applicable add information which helps to understand the situation experienced.',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'当您进入PC三分钟后内是否受到员工的欢迎？\r\n','Were you welcomed within 3 minutes after entering the PC?','一个员工应该专门接待您。如果没有在三分钟内接受到问候的话，请您具体指明等待问候的时间（具体到分钟）\r\n','An employee should be a clear point of contact for you. Please give details in the comment of how long you wait until being greeted, if the greeting was not received within the three minutes  (in minutes)',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'是否给您提供帮助？\r\n','Were you offered assistance?','请具体说明，谁向您提供的帮助，怎样提供的，例如：“我可以为您做些什么吗？”，“您对这款汽车感兴趣吗？”等等。\r\n','Please state in the comment, who offered you assistance and how e.g. \"What can I do for you?\", \"You are interested in this vehicle etc.?\"',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'PC的员工是否友善?\r\n','Was the staff in the PC friendly?\r\n','这倒问题的检测对象是所有的PC员工。\r\n','This question refers to all members of staff at the PC',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'如果有一段等待的时间，PC的员工是否让您感觉舒服呢？\r\n','If there was a waiting time, did the dealership staff make it comfortable for you?','-请您描述一下PC的员工如何过渡这段等待时间的？例如：是否请您坐下，为您提供饮料、递送信息材料如小册子之类.\r\n- 如果没有等待的时间（没有等待时间意味着销售主管立即接待了您），请选择N/A\"','\"Please describe how the dealership staff bridge the waiting time? E.g. You were offered to take a seat and /or offered a beverage, information material was handed out such as for example brochures etc.\r\n- N/A if there was no waiting time, this means the SE was immediately available for you',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'是否有人给您提供饮料？\r\n','Were you offered a beverage?','指的是您在PC的任何时间。也包括您和销售主管咨询的期间。如果给您提供了饮料，选择“是”\r\n','At any time during your stay at the PC. This can also be by the sales executive during the consultation. If this is the case, please also answer, yes.',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (5,'评价（请务必详细作答）\r\n','Comments','请就A3这部分的检测做一个整体评价。恰当信息将帮助我们更深入地理解您所处的情景。\r\n','Please comment, concluding the dimension. If applicable add information which helps to understand the situation experienced.',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'您是否能够很容易的找到保时捷的销售部门？\r\n','Could you easily locate the Porsche sales department?','当您有问题要咨询时，您很清楚或很容易找到销售人员帮助你。\r\n','It was clear to you, who you had to contact for questions.',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否以友善的态度欢迎您？\r\n','Did the Sales Executive welcome you in a friendly manner?','问候要符合社会/文化背景\r\n','Greeting according to the social / cultural context.',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否作了自我介绍，并告诉了您他的名字？\r\n','Did the Sales Executive introduce themselves personally with their name?','','',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否可以通过的衬衫上标签或他桌子上的标牌被辨认出来？\r\n','Could the Sales Executive be identified either by name tag, embroidered name on the shirt or a name plate on his desk?\r\n','-这个问题和销售主管自我介绍是分开考察的；\r\n-如果名片架（可以代替在桌子上的名字标牌），请选y“是”\"\r\n','-This question is independent of the sales executive introducing themselves.；\r\n-Instead of a name plate on the desk, business card holders can be used, this should also be evaluated with yes.',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否试图获得您的姓名？\r\n','Did the Sales Executive try to find out your name?','-请您解释这是怎么发生的，例如：用一个直接的问题。如果您在问候过程中提到了您的名字，销售主管随后在谈话中使用您的名字来称呼您，请选择“是”\r\n','-Please explain how this happened, e.g. with a direct question. Please also answer with yes, if you mention your name during the greeting process and the sales executive consequently uses your name during the conversation.',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否在与您的交谈中，称呼您的名字？\r\n','Did the Sales Executive address you by your name during the sales conversation?','至少使用了一次，请选择“是”\r\n','yes, when at least done once',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否打扮得体，服饰专业？\r\n','Was the Sales Executive well groomed and dressed professionally?','- 干净整洁的衣服；\r\n- 干净的头发，整洁的胡子\r\n- 干净的手和指甲\r\n- 中性的/令人愉快的香水气味\"\r\n','- Clean and neat clothing\r\n- Clean hair, trimmed beard / clean shaven\r\n- Clean hands and nails\r\n- Neutral / pleasant scent',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管的工作区是否干净整洁？\r\n','Was the Sales Executive\'s workplace tidy?','- 周围没有多余的东西放置；\r\n- 没有堆积的文件和纸张\r\n- 干净整洁\r\n- 请详细说明一些不恰当的地方（如果不在给出范围里面）；\r\n- 如果不能看到销售主管的办公区，选择N/A\"\r\n','- no unnecessary objects lying around;\r\n- no pile of files and papers\r\n- clean- Please comment in detail what was not correct, if a no is given.\r\n-N/A if you can not see the workplace of the sales executive',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否尊重您？\r\n','Did the Sales Executive appreciate you?','- 谦虚礼貌\r\n- 亲切的\r\n- 行为举止透露出尊重','- courteous\r\n- obliging\r\n- behaving with respect',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否专心为您一个人服务？\r\n','Did the Sales Executive give you his undivided attention?\r\n','-销售经理没有让任何人或任何事打扰您，例如：接听电话；\r\n-在谈话中，销售主管把全部注意力都放在与您的交谈中；\r\n-如果选择了“否”，请详细说明哪些地方做的不好。\"\r\n','-The SE doesn\'t let anyone or anything disturb you, e.g. Answering the telephone?\r\n-You seemed to have the SE\'s full concentration during the conversation\r\n-please state in detail what was not correct, if a no is given\r\n',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售人员是否具备良好的产品知识，特别是关于卡宴的标准“Sport（运动）”按钮？\r\n','Did the Sales Executive demonstrate good product knowledge,especially concerning the standard \"Sport\" button of the Cayenne?',' 请您在此次检测前，使自己对保时捷汽车有一定的了解。请销售主管为您详细的说明您喜欢的汽车（内部/外部）和消费者能够获得的利益\r\n','Please inform yourself upfront of the test visit about the vehicle. Let the SE explain you in detail your preferred equipment (interior/exterior) and the customer\'s benefit',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'燃油添加剂（快乐跑）是否有陈列在询问处和销售顾问的办公桌上？\r\n','Was the fuel additive (Keropur) displayed on the reception desk and on the sales advisor\'s desk?','','',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售顾问能否解释＂快乐跑＂的使用方法?\r\n','Was the Sales Advisor able to explain the usage of Keropur?','如何使用，使用频率（答案：每一满箱汽油加一瓶＂快乐跑＂）\r\n','how to use it, frequency of usage (add one to every tankful)\r\n',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售顾问在不查看价位手册的情况下，是否知道＂快乐跑＂的正确价格?\r\n','Did the Sales Advisor know the price of one can of Keropur without looking it up?','正确答案是　１２２人民币\r\n','answer is RMB 122',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售顾问能否对顾客解释为什么要购买燃油添加剂？\r\n','Was the Sales Advisor able to explain to the customer why he should buy the fuel additive?','关键词：增强汽油使用效率，更好的燃烧，更好的表现，使汽车高性能表现\r\n','Keywords: fuel enhancement, burns better, better performance, high performance cars etc.',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售顾问能否解释维护日程\r\n','Was the Sales Advisor able to explain the maintenance schedule?\r\n','火花塞每２万公里换一次，机油每１万公里换一次\r\n','spark plug change every 20,000 km, oil change every 10,000 km',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否将您视为一位潜在的购买者来认真对待？\r\n','Did the Sales Executive treat you seriously as a prospective buyer?','- 友好的\r\n- 注意力集中在您身上\r\n- 如果选择“否”请详细解释原因\r\n','- friendly\r\n- focused on you\r\n- please explain in detail why this was not the case, if a no is given\r\n',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否让您在整个参观过程中感到受到欢迎呢？\r\n','Did the Sales Executive make you feel welcome during your visit?','','',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否激发了您对保时捷品牌和产品的兴趣？\r\n','Did the Sales Executive inspire you for brand and product?','','',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否给你一种感觉：他没有赶时间，不存在时间压力？\r\n','Did the SE give you the feeling that he was not under time pressure?','如果销售主管看起来很平静，没有试图很快结束此次销售咨询，请选择“是”\r\n','Positive, if the sales executive seems calm and does not try to end the sales talk quickly',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否允许您离开PC，同时没有给您任何购买压力？\r\n','Did the Sales Executive allow you to leave the PC without feeling under pressure to buy?','销售主管没有催促/迫使您做出立即的决定。\r\n','The SE doesn\'t push you for an immediate decision.',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'评价（请务必详细作答）\r\n','Comments','请就B1这部分的检测做一个整体评价。恰当信息将帮助我们更深入地理解您所处的情景。\r\n','Please comment, concluding the dimension. If applicable add information which helps to understand the situation experienced.',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否确定/探寻了您是对新车还是二手车感兴趣？\r\n','Did the Sales Executive determine whether you are interested in a new or used vehicle?','- 例如：一个直接的问题或者询问您来这里的特殊原因？\r\n','- for example, a direct question or asked if there is a particular reason for your visit',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否确定/探寻了您是想订购一款车子还是直接购买现车？\r\n','Did the Sales Executive determine if you are interested in ordering a vehicle or purchasing from stock?','','',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否确定/探寻了您现在开的是什么汽车？\r\n','Did the Sales Executive determine what vehicle(s) you drive at the moment?','','',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否确定/探寻了您对可选配件的偏好？\r\n','Did the Sales Executive determine your preferences regarding optional equipment?','- 直接询问，例如：引擎大小、自动排档还是手动排档等等。\r\n- 询问关于您当前使用的车俩情况\r\n- 等等','- asked directly e.g. for engine size, automatic or manual transmission etc. \r\n- in reference to your current car\r\n- etc',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否确定/探寻了您使用汽车的目的？\r\n','Did the Sales Executive determine how you want to use the vehicle?\r\n','例如：\r\n- 主力车或第二款车\r\n- 里程数\r\n- 舒适方面的需求\r\n','for example:\r\n- main or second car\r\n- mileage\r\n- comfort requirements',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否探寻了您的购买预算？\r\n','Did the Sales Executive find out your financial scope for the purchase?','例如：问您是不是有特定的购车预算？\r\n','for example, asks if you\'ve set yourself a particular budget',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否主动向您介绍了保时捷的客户定制特色\r\n','Did the Sales Executive actively bring up Porsche tailoring possibilities?','-定制装配项目（轮胎、外饰、内饰、音响/通讯等）\r\n-来自工厂的特殊设计（外饰/内饰套餐、增强的性能等）\r\n','-Tequipment=accessory (tyres, exterior, interior,sudio/communication etc.)Tequipment\r\n-Exclusive=special designs from factory (exterior/interior packages, increased performance etc.) Exclusive',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否发现了您对保时捷产品的客户定制特色的潜在兴趣\r\n','Did the Sales Executive discover your potential regarding vehicle tailoring features?','- 向您介绍了Tequipment和Exclusive\r\n- 销售主管是否注意到了您的反应，并提供了一项合适的，而您以前又没有考虑过的附加特色\r\n','- Explanation of Tequipment, Exclusive\r\n- SE took in your reaction and offered where applicable an additional feature that you did not consider previously',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售经理是否用了一种易于理解的方式，向您解释了保时捷的客户定制特色。\r\n','Did the Sales Executive explain the Porsche tailoring possibilities in an understandable way?','- 让销售主管详细介绍一项客户定制服务\r\n-合理的语速\r\n- 没有不必要的行话\r\n-解释技术术语\r\n-您有一种感觉：您理解了客户可以从此项服务中（客户定制服务）中获得的利益\r\n-如果没有提到客户定制服务的话，请选择N/A\"\r\n','- let the SE explain one tailoring possibility in more detail\r\n-speaks at a reasonable speed\r\n-no unnecessary jargon\r\n-explanation of technical terms\r\n-you have the feeling that you understood the customer\'s benefit\r\n-N/A, if no tailoring possibilities were offered',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否发现/探寻了您心目中的竞争对手？\r\n','Did the Sales Executive find out about your considered competitors?','-例如：询问您是否参观了其他汽车经销店\r\n- 询问您当前的车型，是否还想使用同一品牌的车子。\r\n','-for example, asks if you had visited any other car dealerships\r\n- ask about your current car and whether you want to stay with the same brand ',1,'','',1);
