INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否定位/记录了您在交谈当中所涉及到需求 \r\n','Did the Sales Executive address your given needs during the consultation?','- 销售主管是否注意到了您所提到的信息\r\n- 销售主管是否帮助您确定了您心目中理想车型的关键特色，并提供特定的设备满足您的需求。\"\r\n','- the SE took the information in\r\n- the SE helps you identify the key features of your ideal car and offers a specific equipment, which matches your requirements',1,'','',1);
INSERT INTO question_info (questiontype,question,englishquestion,zhexplain,englishexplain,fieldtype,fieldvalue,format,status)VALUES (2,'销售主管是否试图销售给您一辆特殊设计的车子来匹配你所提到的喜好？ \r\n','Did the Sales Executive try to sell you exclusively a vehicle that suited your mentioned preferences? \r\n','销售经理没有试图现有库存中销售给您一辆现车，来匹配您的需求。\r\n','The SE does not try to sell you a vehicle from the stock that nearly matches your requirements',1,'','',1);
